import React, { Component } from "react";
import uniqid from "uniqid";
import Overview from "./components/Overview";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tasks: [],
    };

    this.addTask = this.addItem.bind(this);
    this.removeTask = this.removeitem.bind(this);
  }

  addItem(event) {
    event.preventDefault(); //Prevents page refresh

    this.setState({
      tasks: [
        ...this.state.tasks,
        { value: event.target.taskname.value, id: uniqid() },
      ],
    });

    event.target.reset(); //resets the text field after submit
  }

  removeitem(id)  {
    // /const index = this.state.tasks.indexOf
  }

  render() {
    return (
      <div>
        <form onSubmit={this.addTask}>
          <label>
            Enter a new task :{" "}
            <input id="task-input" type="text" name="taskname" />
          </label>
          <button type="submit">Add Task</button>
        </form>

        <Overview tasks={this.state.tasks} removeItem={this.removeitem}/>
      </div>
    );
  }
}

export default App;
