import React, { Component } from "react";
import Overview from "./components/Overview";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tasks: [],
    };
  }

  addItem(newItem) {
    this.setState({
      tasks: [...this.state.tasks, newItem],
    });
  }

  render() {
    return (
      <div>
        <Overview tasks={this.state.tasks} />
      </div>
    );
  }
}

export default App;
