import React, { Component } from "react";
import Overview from "./components/Overview";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tasks : []
    };
  }

  addItem(newItem) {
    this.setState({
      tasks : [...this.state.tasks, newItem]
    });
  }

  render() {
    return <Overview />;
  }
}

export default App;
